import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom/client';
import { supabase } from './supabaseClient';
import './index.css';

const App = () => {
    const [session, setSession] = useState(null);
    const [isLoginView, setIsLoginView] = useState(true);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [displayName, setDisplayName] = useState('');

    useEffect(() => {
        setSession(supabase.auth.getSession());

        supabase.auth.onAuthStateChange((_event, session) => {
            setSession(session);
        });
    }, []);

    const handleLogin = async (e) => {
        e.preventDefault();
        const { error } = await supabase.auth.signInWithPassword({ email, password });
        if (error) alert(error.message);
    };

    const handleSignUp = async (e) => {
        e.preventDefault();
        const { error } = await supabase.auth.signUp({
            email,
            password,
            options: {
                data: {
                    display_name: displayName,
                },
            },
        });
        if (error) {
            alert(error.message);
        } else {
            alert('Check your email for the login link!');
        }
    };

    const handleLogout = async () => {
        await supabase.auth.signOut();
    };

    const getSurname = () => {
        if (session?.user?.user_metadata?.display_name) {
            const nameParts = session.user.user_metadata.display_name.split(' ');
            return nameParts.length > 1 ? nameParts[nameParts.length - 1] : nameParts[0];
        }
        return 'User';
    };

    return (
        <div className="app-container">
            <header className="app-header">
                <h1>Task Manager</h1>
            </header>
            <main className="main-content">
                {session ? (
                    <div className="logged-in-container">
                        <h2>Welcome, {getSurname()}!</h2>
                        <p>You are logged in.</p>
                        <button onClick={handleLogout}>Logout</button>
                    </div>
                ) : (
                    <div>
                        <h2>{isLoginView ? 'Login' : 'Create Account'}</h2>
                        <div className="form-container">
                            <form onSubmit={isLoginView ? handleLogin : handleSignUp}>
                                {!isLoginView && (
                                    <input
                                        type="text"
                                        placeholder="Display Name"
                                        value={displayName}
                                        onChange={(e) => setDisplayName(e.target.value)}
                                        required
                                    />
                                )}
                                <input
                                    type="email"
                                    placeholder="Email"
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                    required
                                />
                                <input
                                    type="password"
                                    placeholder="Password"
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                    required
                                />
                                <button type="submit">{isLoginView ? 'Login' : 'Create Account'}</button>
                            </form>
                            <p className="toggle-form" onClick={() => setIsLoginView(!isLoginView)}>
                                {isLoginView ? 'Need an account? Create one' : 'Already have an account? Login'}
                            </p>
                        </div>
                    </div>
                )}
            </main>
        </div>
    );
};

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
    <React.StrictMode>
        <App />
    </React.StrictMode>
);
